import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { 
  ExternalLink, 
  Github, 
  Eye,
  Filter,
  Code,
  Smartphone,
  Brain,
  Cloud
} from 'lucide-react';

const PortfolioSection = styled.section`
  padding: 128px 24px;
  background: var(--background-primary);
  position: relative;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 80px;
`;

const SectionTitle = styled.h2`
  font-size: clamp(36px, 5vw, 48px);
  font-weight: 700;
  color: var(--text-primary);
  margin-bottom: 16px;
  background: var(--gradient-primary);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const SectionSubtitle = styled.p`
  font-size: 18px;
  color: var(--text-secondary);
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
`;

const FilterContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 16px;
  margin-bottom: 48px;
  flex-wrap: wrap;

  @media (max-width: 768px) {
    gap: 12px;
  }
`;

const FilterButton = styled.button`
  background: ${props => props.active ? 'var(--primary-main)' : 'transparent'};
  color: ${props => props.active ? 'var(--text-inverse)' : 'var(--primary-main)'};
  border: 2px solid var(--primary-main);
  padding: 12px 24px;
  border-radius: 25px;
  font-weight: 600;
  font-size: 14px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: var(--primary-main);
    color: var(--text-inverse);
    transform: translateY(-2px);
  }
`;

const PortfolioGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 32px;
  margin-bottom: 64px;
`;

const PortfolioCard = styled(motion.div)`
  background: var(--background-card);
  border-radius: 16px;
  overflow: hidden;
  border: 1px solid var(--border-primary);
  transition: all 0.3s ease;
  cursor: pointer;

  &:hover {
    transform: translateY(-8px);
    box-shadow: 0 20px 40px var(--shadow-medium);
    border-color: var(--primary-light);
  }
`;

const PortfolioImage = styled.div`
  position: relative;
  height: 240px;
  background: var(--gradient-background);
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.3s ease;
  }

  .overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(96, 165, 250, 0.9);
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 16px;
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  &:hover {
    .overlay {
      opacity: 1;
    }

    img {
      transform: scale(1.1);
    }
  }
`;

const OverlayButton = styled.button`
  background: var(--text-inverse);
  color: var(--primary-main);
  border: none;
  width: 48px;
  height: 48px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: scale(1.1);
    background: var(--background-secondary);
  }
`;

const PortfolioContent = styled.div`
  padding: 24px;
`;

const PortfolioTitle = styled.h3`
  font-size: 20px;
  font-weight: 600;
  color: var(--text-primary);
  margin-bottom: 12px;
`;

const PortfolioDescription = styled.p`
  color: var(--text-secondary);
  line-height: 1.6;
  margin-bottom: 20px;
  font-size: 14px;
`;

const PortfolioTags = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-bottom: 20px;
`;

const PortfolioTag = styled.span`
  background: var(--background-secondary);
  color: var(--text-secondary);
  padding: 4px 12px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 500;
`;

const PortfolioLinks = styled.div`
  display: flex;
  gap: 12px;
`;

const PortfolioLink = styled.a`
  display: flex;
  align-items: center;
  gap: 8px;
  color: var(--primary-main);
  text-decoration: none;
  font-size: 14px;
  font-weight: 500;
  transition: color 0.3s ease;

  &:hover {
    color: var(--primary-light);
  }
`;

const CTAButton = styled.button`
  background: var(--gradient-primary);
  color: var(--text-inverse);
  border: none;
  padding: 16px 32px;
  border-radius: 8px;
  font-weight: 600;
  font-size: 16px;
  cursor: pointer;
  transition: all 0.3s ease;
  display: block;
  margin: 0 auto;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 12px 30px var(--shadow-colored);
  }
`;

const projects = [
  {
    id: 1,
    title: "E-Commerce Platform",
    description: "A modern, scalable e-commerce platform built with React and Node.js, featuring advanced search, payment integration, and admin dashboard.",
    image: "/assets/LOGO.png",
    category: "web",
    technologies: ["React", "Node.js", "MongoDB", "Stripe"],
    liveUrl: "#",
    githubUrl: "#"
  },
  {
    id: 2,
    title: "AI-Powered Analytics Dashboard",
    description: "Intelligent business analytics platform using machine learning to provide actionable insights and predictive analytics.",
    image: "/assets/LOGO.png",
    category: "ai",
    technologies: ["Python", "TensorFlow", "React", "PostgreSQL"],
    liveUrl: "#",
    githubUrl: "#"
