import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Menu, X } from 'lucide-react';

const HeaderContainer = styled.header`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
`;

const HeaderContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 24px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 80px;
`;

const Logo = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;

  img {
    height: 40px;
    width: auto;
  }

  span {
    font-size: 24px;
    font-weight: 700;
    background: linear-gradient(135deg, #1E3A8A 0%, #3B82F6 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
`;

const Navigation = styled.nav`
  display: flex;
  gap: 32px;
  align-items: center;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavLink = styled.a`
  text-decoration: none;
  color: #1a1a1a;
  font-weight: 500;
  font-size: 14px;
  transition: color 0.3s ease;
  position: relative;

  &:hover {
    color: #3B82F6;
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -4px;
    left: 0;
    width: 0;
    height: 2px;
    background: #3B82F6;
    transition: width 0.3s ease;
  }

  &:hover::after {
    width: 100%;
  }
`;

const CTAButton = styled.button`
  background: #1E3A8A;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 6px;
  font-weight: 600;
  font-size: 14px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: #3B82F6;
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(59, 130, 246, 0.3);
  }
`;

const MobileMenuButton = styled.button`
  display: none;
  background: none;
  border: none;
  cursor: pointer;
  color: #1a1a1a;

  @media (max-width: 768px) {
    display: block;
  }
`;

const MobileMenu = styled(motion.div)`
  position: fixed;
  top: 80px;
  left: 0;
  right: 0;
  background: white;
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
  padding: 24px;
  display: flex;
  flex-direction: column;
  gap: 16px;

  @media (min-width: 769px) {
    display: none;
  }
`;

const MobileNavLink = styled.a`
  text-decoration: none;
  color: #1a1a1a;
  font-weight: 500;
  font-size: 16px;
  padding: 12px 0;
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);

  &:hover {
    color: #3B82F6;
  }
`;

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setIsMobileMenuOpen(false);
  };

  return (
    <HeaderContainer style={{ 
      background: isScrolled ? 'rgba(255, 255, 255, 0.98)' : 'rgba(255, 255, 255, 0.95)',
      boxShadow: isScrolled ? '0 4px 20px rgba(0, 0, 0, 0.1)' : 'none'
    }}>
      <HeaderContent>
        <Logo>
          <img src="/assets/LOGO.png" alt="TechPillar" />
          <span>TechPillar</span>
        </Logo>

        <Navigation>
          <NavLink href="#services" onClick={() => scrollToSection('services')}>
            Services
          </NavLink>
          <NavLink href="#about" onClick={() => scrollToSection('about')}>
            About
          </NavLink>
          <NavLink href="#portfolio" onClick={() => scrollToSection('portfolio')}>
            Portfolio
          </NavLink>
          <NavLink href="#contact" onClick={() => scrollToSection('contact')}>
            Contact
          </NavLink>
          <CTAButton>Get Quote</CTAButton>
        </Navigation>

        <MobileMenuButton onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}>
          {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
        </MobileMenuButton>
      </HeaderContent>

      {isMobileMenuOpen && (
        <MobileMenu
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          transition={{ duration: 0.3 }}
        >
          <MobileNavLink href="#services" onClick={() => scrollToSection('services')}>
            Services
          </MobileNavLink>
          <MobileNavLink href="#about" onClick={() => scrollToSection('about')}>
            About
          </MobileNavLink>
          <MobileNavLink href="#portfolio" onClick={() => scrollToSection('portfolio')}>
            Portfolio
          </MobileNavLink>
          <MobileNavLink href="#contact" onClick={() => scrollToSection('contact')}>
            Contact
          </MobileNavLink>
          <CTAButton style={{ marginTop: '16px' }}>Get Quote</CTAButton>
        </MobileMenu>
      )}
    </HeaderContainer>
  );
};

export default Header;
